mapper.select:
select emp_name empName, emp_id empId, emp_pw empPw, emp_addr empAddr, emp_phone empPhone
from employees
where emp_id = #{empId}

mapper.update:
update employees
set emp_name = #{empName}, emp_id = #{empId}, emp_addr = #{empAddr}, emp_pw = #{empPw},  emp_phone = #{empPhone}
where emp_id = #{empId}
